çš„å†…éƒ¨**ï¼Œè€Œä¸”ä¸åœ¨å®šä¹‰ç±»çš„å®šä¹‰åŸŸä¹‹å†…ä¾¿æ— æ³•ä½¿ç”¨ï¼Œå…¶æä¾›çš„åŠŸèƒ½ä½¿ç”¨åŒ¿åå†…éƒ¨ç±»éƒ½å¯ä»¥å®ç°ï¼Œè€Œæœ¬èº«åŒ¿åå†…éƒ¨ç±»å¯ä»¥å†™å¾—æ¯”å®ƒæ›´ç®€æ´ï¼Œå› æ­¤å±€éƒ¨å†…éƒ¨ç±»ç”¨çš„æ¯”è¾ƒå°‘ã€‚\n```java\npublic class InnerClassTest {\n\n    public int field1 = 1;\n    protected int field2 = 2;\n    int field3 = 3;\n    private int field4 = 4;\n\n    public InnerClassTest() {\n        System.out.println(\"åˆ›å»º \" + this.getClass().getSimpleName() + \" å¯¹è±¡\");\n    }\n    \n    private void localInnerClassTest() {\n	    // å±€éƒ¨å†…éƒ¨ç±» Aï¼Œåªèƒ½åœ¨å½“å‰æ–¹æ³•ä¸­ä½¿ç”¨\n        class A {\n	        // static int field = 1; // ç¼–è¯‘é”™è¯¯ï¼å±€éƒ¨å†…éƒ¨ç±»ä¸­ä¸èƒ½å®šä¹‰ static å­—æ®µ\n            public A() {\n	            System.out.println(\"åˆ›å»º \" + A.class.getSimpleName() + \" å¯¹è±¡\");\n                System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field1 å­—æ®µçš„å€¼ä¸º: \" + field1);\n                System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field2 å­—æ®µçš„å€¼ä¸º: \" + field2);\n                System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field3 å­—æ®µçš„å€¼ä¸º: \" + field3);\n                System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field4 å­—æ®µçš„å€¼ä¸º: \" + field4);\n            }\n        }\n        A a = new A();\n        if (true) {\n	        // å±€éƒ¨å†…éƒ¨ç±» Bï¼Œåªèƒ½åœ¨å½“å‰ä»£ç å—ä¸­ä½¿ç”¨\n            class B {\n                public B() {\n	                System.out.println(\"åˆ›å»º \" + B.class.getSimpleName() + \" å¯¹è±¡\");\n                    System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field1 å­—æ®µçš„å€¼ä¸º: \" + field1);\n                    System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field2 å­—æ®µçš„å€¼ä¸º: \" + field2);\n                    System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field3 å­—æ®µçš„å€¼ä¸º: \" + field3);\n                    System.out.println(\"å…¶å¤–éƒ¨ç±»çš„ field4 å­—æ®µçš„å€¼ä¸º: \" + field4);\n                }\n            }\n            B b = new B();\n        }\n//        B b1 = new B(); // ç¼–è¯‘é”™è¯¯ï¼ä¸åœ¨ç±» B çš„å®šä¹‰åŸŸå†…ï¼